    const [showOnglet, setshowOnglet] = useState([true,false,false,false]);  


    useEffect(() => {
        //getListeEntreprise();
    }, []);

    return (
        <div className='flex flex-col  w-8/12 bg-yellow-400'>
            <div className='flex flex-row bg-red-400'>
            {nomsOnglets.map((key, index) => (
                showOnglet.at(index) ?
            <div id={key} className='flex flex-col  w-8/12 bg-green-400'> 
            {nomsOnglets.at(index)}
               </div> 
         :
